mapper.select:
SELECT LEC_NUM, mimetype, original_name, data, regdate, update_regdate
FROM lecture_image
WHERE LEC_NUM = #{lecNum}

mapper.update:
UPDATE lecture_image
SET mimetype = #{mimetype}, original_name = #{originalName}, data = #{data}, update_regdate = SYSDATE
WHERE LEC_NUM = #{lecNum}

UPDATE lecture_image
SET delete_YN_code = 'Y'
WHERE LEC_NUM = #{lecNum}
