mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM as rnum FROM (SELECT LEC_NUM, TEACHER, LEC_WRITER, TITLE, DESCRIBE ,VIDEOPATH, VIEWCOUNT, USER_REGDATE, LARGE_CATEGORY, SMALL_CATEGORY FROM LECTURE
WHERE LARGE_CATEGORY=#{largeCategory}
WHERE LARGE_CATEGORY=#{largeCategory} AND SMALL_CATEGORY=#{smallCategory}
ORDER BY LEC_NUM DESC) result1) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE LECTURE
SET VIEWCOUNT=VIEWCOUNT+1
WHERE LEC_NUM=#{lecNum}

UPDATE LECTURE
SET TITLE= #{TITLE}, TEACHER=#{TEACHER},DESCRIBE=#{DESCRIBE}, VIDEOPATH=#{VIDEOPATH}, LARGE_CATEGORY=#{largeCategory},SMALL_CATEGORY=#{smallCategory}
WHERE LEC_NUM = #{lecNum}

UPDATE LECTURE
SET SERVICE_YN_CODE='N'
WHERE LEC_NUM=#{lecNum}
