mapper.update:
UPDATE LECTURE
SET VIEWCOUNT=VIEWCOUNT+1
WHERE LEC_NUM=#{lecNum}

UPDATE LECTURE
SET TITLE= #{title},UPDATE_ID=#{updateId} ,TEACHER=#{teacher},DESCRIBE=#{describe}, VIDEOPATH=#{videoPath}, UPDATE_DATE=SYSDATE, LARGE_CATEGORY=#{largeCategory},SMALL_CATEGORY=#{smallCategory}
WHERE LEC_NUM = #{lecNum}

UPDATE LECTURE
SET SERVICE_YN_CODE='N'
WHERE LEC_NUM=#{lecNum}
