mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM as rnum FROM (SELECT users.LEC_USER_ID,TO_CHAR(birth,'YYYY-MM-DD') birth, phone, email, User_Regdate, large_category, small_category FROM users JOIN LECTURE_STUDENT ON users.LEC_USER_ID=LECTURE_STUDENT.lec_Stu_User_Id JOIN LECTURE on LECTURE.lec_Num=LECTURE_STUDENT.lec_Stu_Ref_Group
AND lec_Stu_User_Id LIKE '%'||#{lecStuUserId}||'%' AND birth LIKE '%'||#{birth}||'%' AND phone LIKE '%'||#{phone}||'%' AND email LIKE '%'||#{email}||'%' AND userRegdate LIKE '%'||#{userRegdate}||'%' AND large_category=#{largeCategory} AND large_category=#{largeCategory} AND small_category=#{smallCategory}
ORDER BY users.LEC_USER_ID DESC) result1)
WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE LECTURE_STUDENT
SET Complete_Yn=#{completeYn}, Update_Date=SYSDATE
WHERE lec_Stu_Num=#{lecStuNum}
