mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM as rnum FROM (SELECT users.id,TO_CHAR(birth,'YYYY-MM-DD') birth, phone, email, User_Regdate, large_category, small_category FROM users JOIN LECTURE_STUDENT ON users.id=LECTURE_STUDENT.Lec_Stu_User_Id JOIN LECTURE on LECTURE.lec_Num=LECTURE_STUDENT.lec_Stu_Ref_Group
AND Lec_Stu_User_Id LIKE '%'||#{Lec_Stu_User_Id}||'%' AND birth LIKE '%'||#{birth}||'%' AND phone LIKE '%'||#{phone}||'%' AND email LIKE '%'||#{email}||'%' AND User_Regdate LIKE '%'||#{User_Regdate}||'%' AND large_category=#{large_category} AND large_category=#{large_category} AND small_category=#{small_category}
ORDER BY users.id DESC) result1)
WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}
