mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT noti_num, title, viewCount, regdate, noti_writer, delete_yn_code FROM NOTICE
delete_yn_code = 'N' AND (title LIKE '%'||#{title}||'%' OR content LIKE '%'||#{content}||'%') AND title LIKE '%'||#{title}||'%'
ORDER BY noti_num DESC) result1) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

mapper.update:
UPDATE NOTICE


		title = #{title},


		content=#{content},

	update_regdate = SYSDATE,
	update_id = #{update_id}

WHERE noti_num=#{noti_num}
