mapper.select:
SELECT lec_user_id, mimetype, original_name, data, regdate, update_regdate
FROM profile_image
WHERE lec_user_id = #{lecUserId}

mapper.update:
UPDATE profile_image
SET mimetype = #{mimetype}, original_name = #{originalName}, data = #{data}, update_regdate = SYSDATE
WHERE lec_user_id = #{lecUserId}

UPDATE profile_image
SET delete_YN_code = 'Y'
WHERE lec_user_id = #{lecUserId}
