mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT free_question_num,free_question_writer,title,content,viewCount,user_regdate,answered_yn FROM qna_free_question
delete_YN_code='N'
ORDER BY free_question_num DESC) result1) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

SELECT result1.* FROM (SELECT free_question_num, title, content, viewCount, user_regdate, free_question_writer, delete_YN_code, update_date, LAG(free_question_num, 1, 0) OVER (ORDER BY free_question_num DESC) nextNum, LEAD(free_question_num, 1, 0) OVER (ORDER BY free_question_num DESC) prevNum FROM qna_free_question
delete_YN_code='N'
) result1 WHERE free_question_num=#{freeQuestionNum}

mapper.update:
UPDATE qna_free_question
SET delete_Yn_code='Y'
WHERE free_question_num=#{freeQuestionNum}

UPDATE QNA_FREE_QUESTION
SET answered_YN='Y'
WHERE free_question_num=#{freeQuestionNum}

UPDATE QNA_FREE_QUESTION
SET viewcount = viewcount+1
WHERE free_question_num=#{freeQuestionNum}
