mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT lec_user_id, user_email, user_regdate, user_phone, user_birth, user_nickname FROM USERS
manager_yn = 'N' AND general_user_code = 'Y'
ORDER BY user_regdate DESC) result1) WHERE rnum BETWEEN #{rowStart} AND #{rowEnd}

		SELECT lec_user_id, user_pwd, user_email, user_regdate, user_phone, user_birth, user_nickname, manager_yn
        FROM USERS
        WHERE lec_user_id=#{lecUserId}

mapper.update:
	UPDATE USERS

		, user_email=#{userEmail}
		, user_phone=#{userPhone}
		, user_nickname=#{userNickname}

    WHERE lec_user_id=#{lecUserId}

	UPDATE USERS
	SET user_pwd=#{newPwd}
    WHERE lec_user_id=#{lecUserId}

	UPDATE USERS
	SET user_last_date = SYSDATE, REST_USER_CODE='N', general_user_code = 'Y'
    WHERE lec_user_id=#{lecUserId}

	UPDATE USERS
	SET withdrewal_user_code='Y', general_user_code = 'N'
    WHERE lec_user_id=#{lecUserId}
